import AppAttest
import Foundation
import Testing

@Test(arguments: [
  //Request.appleSample,
  Request.sample1,
  Request.sample2,
])
func verifyAppAttest(request: Request) async throws {
  let appAttest = AppAttest(
    teamId: request.teamId,
    bundleId: request.bundleId
  )
    
  _ = try await appAttest.verify(
    challenge: request.challenge,
    keyId: request.keyId,
    attestation: request.attestation,
    environment: request.environment
  )
}

struct Request {
  var challenge: Data
  var keyId: Data
  var attestation: Data
  var teamId: String
  var environment: Environment
  var bundleId: String
  
  init(
    challenge: String,
    teamId: String,
    bundleId: String,
    environment: Environment,
    keyId: String,
    attestation: String
  ) {
    self.challenge = Data(base64Encoded: challenge)!
    self.keyId = Data(base64Encoded: keyId)!
    self.attestation = Data(base64Encoded: attestation)!
    self.teamId = teamId
    self.bundleId = bundleId
    self.environment = environment
  }
}

